roll=function(){startRoll();var t=$(".die-faces"),n=0;$(".dice").each(function(){var i=$(this),r=i.find(".die-value"),u=parseInt(i.find(".die-faces").attr("numfaces"));$.get("/api/Roll/"+u,function(i){r.text(i);n++;n===t.length&&endRoll()})})};startRoll=function(){$("#rollBtn").addClass("disabled");$(".die-value").removeClass("visible");$(".die-value").addClass("hidden")};endRoll=function(){$("#rollBtn").removeClass("disabled");$(".die-value").removeClass("hidden");$(".die-value").addClass("visible")};setDice=function(){for(var t,r,i=Array.from(arguments),n=0;n<3;n++)t=$(".dice").eq(n),n>=i.length?t.addClass("hidden"):(t.removeClass("hidden"),r=t.find(".die-faces"),r.attr("numfaces",i[n]),r.text("D"+i[n]));roll()};